{"title":"Free_or_Paid_favorites_count","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "title": "Average favorites count for free and paid patterns",
  "data": {
    "name": "vals",
    "url": {
      "%context": "true",
      "index": "patterns",
      "body": {
        "size": 10000,
        "aggs": {
          "free": {
            "terms": {"field": "free"},
            "aggs": {
              "favorites_count": {
                "avg": {
                  "field":"favorites_count"
                }
              }
            }
          }
        }
      }
    },
    "format": {"property": "aggregations.free.buckets"}
  },
  "transform": [{"calculate": "datum.key == 0 ? 'Paid' : 'Free'", as: "avail"}],
  "mark": "bar",
  "encoding": {
    "x": {
      "field": "avail",
      "type": "nominal",
      "axis": {"labelAngle": 0, "title": "Free or Paid"}
    },
    "y": {
      "field": "favorites_count.value",
      "type": "quantitative",
      "axis": {"title": "Average favorites count"}
    }
  }
}"}}
